'use strict';

var login = function ()
{

    this.form = function ()
    {
        window.setInterval(function () {

            var a = $("#username");
            var b = $("#password");
            var c = $("button[type=submit]");

            if (!a.val().trim() || !b.val().trim())
            {
                c.prop("disabled", true);
            } else
            {
                c.prop("disabled", false);
            }


        }, 500);
    };
    
    

};

